// Format meeting summary for Telegram
const extractNode = $('‚úÖ Extract Actions').first().json;
const chatId = $('Process Updates').first().json.chatId;

const summary = extractNode.summary || 'No summary available';
const actionItems = extractNode.action_items || [];
const totalActions = actionItems.length;

// Format message
let message = `üìù *Meeting Summary*\n\n${summary}\n\n`;

if (totalActions > 0) {
  message += `‚úÖ *Action Items* (${totalActions}):\n`;

  // Show ALL action items (removed the limit)
  for (let i = 0; i < actionItems.length; i++) {
    const item = actionItems[i];
    // Truncate long items to fit Telegram message limits
    const displayItem = item.length > 150 ? item.substring(0, 147) + '...' : item;
    message += `\n${i + 1}. ${displayItem}`;
  }

  message += `\n\nüìÖ *Calendar events created!*`;
} else {
  message += `‚ÑπÔ∏è No action items with dates found.`;
}

return [{
  json: {
    chatId: chatId.toString(),
    text: message,
    parse_mode: 'Markdown'
  }
}];

// Parse dates from action items - SUPPORT BOTH TIMED & ALL-DAY EVENTS (TIMEZONE-AWARE)
const input = $input.all()[0].json;
const actionItems = input.action_items;
const summary = input.summary;
const chatId = $('Process Updates').first().json.chatId;

console.log('=== PARSING DATES (TIMED & ALL-DAY) - TIMEZONE: Asia/Hong_Kong ===');
console.log('Action items:', actionItems.length);

const now = new Date();
const currentYear = now.getFullYear();
const currentMonth = now.getMonth();
const currentDay = now.getDate();

// TIMEZONE: Change this to your local timezone
const TIMEZONE = 'Asia/Hong_Kong';
const TIMEZONE_OFFSET = '+08:00';

function parseDateFromText(text, idx) {
  console.log(`\n[${idx+1}] Processing: "${text.substring(0, 80)}..."`);

  // Pattern 1: Month + Day + Year (optional)
  const monthNameRegex = /(January|February|March|April|May|June|July|August|September|October|November|December)\s+(\d{1,2})(?:,?\s+(\d{4}))?/i;
  const dateMatch = text.match(monthNameRegex);

  if (!dateMatch) {
    console.log(`  ‚úó No date found`);
    return null;
  }

  const monthNames = ['january', 'february', 'march', 'april', 'may', 'june', 
                      'july', 'august', 'september', 'october', 'november', 'december'];
  const monthName = dateMatch[1].toLowerCase();
  const day = parseInt(dateMatch[2]);
  let year = dateMatch[3] ? parseInt(dateMatch[3]) : null;

  const monthIndex = monthNames.indexOf(monthName);

  if (monthIndex === -1) {
    console.log(`  ‚úó Invalid month name`);
    return null;
  }

  // Smart year detection
  if (!year) {
    if (monthIndex < currentMonth) {
      year = currentYear + 1;
    } else if (monthIndex === currentMonth) {
      year = day < currentDay ? currentYear + 1 : currentYear;
    } else {
      year = currentYear;
    }
  }

  console.log(`  ‚Üí Date: ${monthName} ${day}, ${year}`);

  // ‚úÖ CHECK FOR TIME in the action item text
  // Pattern: "at 2 PM", "at 10:30 AM", "at 14:00", "@ 3pm"
  const timeRegex = /(?:at|@)\s*(\d{1,2})(?::(\d{2}))?\s*(AM|PM|am|pm)?/i;
  const timeMatch = text.match(timeRegex);

  if (timeMatch) {
    // TIME FOUND - Create timed event
    let hours = parseInt(timeMatch[1]);
    const minutes = timeMatch[2] ? parseInt(timeMatch[2]) : 0;
    const meridiem = timeMatch[3] ? timeMatch[3].toUpperCase() : null;

    // Convert to 24-hour format
    if (meridiem === 'PM' && hours !== 12) {
      hours += 12;
    } else if (meridiem === 'AM' && hours === 12) {
      hours = 0;
    }

    console.log(`  ‚úì TIME FOUND: ${hours}:${minutes.toString().padStart(2, '0')}`);

    // Format date/time strings for Google Calendar (RFC3339 format with timezone)
    const startDateTime = `${year}-${(monthIndex + 1).toString().padStart(2, '0')}-${day.toString().padStart(2, '0')}T${hours.toString().padStart(2, '0')}:${minutes.toString().padStart(2, '0')}:00${TIMEZONE_OFFSET}`;

    // End time = rest of the day (11:59 PM same day)
    const endDateTime = `${year}-${(monthIndex + 1).toString().padStart(2, '0')}-${day.toString().padStart(2, '0')}T23:59:00${TIMEZONE_OFFSET}`;

    console.log(`  ‚Üí Start: ${startDateTime}`);
    console.log(`  ‚Üí End: ${endDateTime}`);

    return {
      start: startDateTime,
      end: endDateTime,
      isAllDay: false,
      hasTime: true
    };
  } else {
    // NO TIME - Create all-day event
    console.log(`  ‚úì NO TIME - Creating all-day event`);

    const dateOnly = `${year}-${(monthIndex + 1).toString().padStart(2, '0')}-${day.toString().padStart(2, '0')}`;

    console.log(`  ‚Üí All-day: ${dateOnly}`);

    return {
      start: dateOnly,
      end: dateOnly,
      isAllDay: true,
      hasTime: false
    };
  }
}

const results = [];
let timedCount = 0;
let allDayCount = 0;
let skippedCount = 0;

for (let i = 0; i < actionItems.length; i++) {
  const actionText = actionItems[i];
  const dateInfo = parseDateFromText(actionText, i);

  if (dateInfo) {
    const title = actionText.length > 100 
      ? actionText.substring(0, 97) + '...'
      : actionText;

    // Use different emoji based on event type
    const emoji = dateInfo.hasTime ? '‚è∞' : 'üìã';

    results.push({
      json: {
        title: `${emoji} ${title}`,
        start: dateInfo.start,
        end: dateInfo.end,
        summary: summary,
        original_action: actionText,
        is_all_day: dateInfo.isAllDay,
        chatId: chatId
      }
    });

    if (dateInfo.hasTime) {
      timedCount++;
    } else {
      allDayCount++;
    }
  } else {
    skippedCount++;
  }
}

console.log(`\n========================================`);
console.log(`‚è∞ TIMED EVENTS: ${timedCount}`);
console.log(`üìã ALL-DAY EVENTS: ${allDayCount}`);
console.log(`‚úó SKIPPED (no date): ${skippedCount}`);
console.log(`========================================\n`);

return results;

// Extract action items from SPECIFIC sections ONLY - PRESERVE TIME FORMAT
const input = $input.all()[0].json;

const formattedNotes = input.formatted_notes;
const summary = input.summary;

console.log('=== EXTRACTING ACTION ITEMS (STRICT MODE - PRESERVE TIME) ===');
console.log('Notes length:', formattedNotes.length);

// ONLY extract from these specific sections
const actionSectionPatterns = [
  /ACTION ITEMS?:\s*([\s\S]*?)(?=\n\n[A-Z]{2,}|$)/i,
  /TASK ASSIGNMENTS?:\s*([\s\S]*?)(?=\n\n[A-Z]{2,}|$)/i,
  /FOLLOW[-\s]?UP ITEMS?:\s*([\s\S]*?)(?=\n\n[A-Z]{2,}|$)/i,
  /TO[-\s]?DO LIST:\s*([\s\S]*?)(?=\n\n[A-Z]{2,}|$)/i,
  /DELIVERABLES?:\s*([\s\S]*?)(?=\n\n[A-Z]{2,}|$)/i,
  /NEXT STEPS?:\s*([\s\S]*?)(?=\n\n[A-Z]{2,}|$)/i
];

let actionItems = [];

// Try each section pattern
for (const pattern of actionSectionPatterns) {
  const sectionMatch = formattedNotes.match(pattern);

  if (sectionMatch) {
    const sectionName = sectionMatch[0].split(':')[0].trim();
    const sectionContent = sectionMatch[1];
    console.log(`\n‚úì Found section: "${sectionName}"`);
    console.log(`  Content length: ${sectionContent.length} chars`);

    // Split by newlines and process each line
    const lines = sectionContent.split('\n');
    let itemsFound = 0;

    for (const line of lines) {
      const trimmed = line.trim();

      // Skip empty lines or very short lines
      if (!trimmed || trimmed.length < 10) continue;

      // Check if line starts with bullet point or dash
      if (trimmed.match(/^[-‚Ä¢*]\s+/)) {
        const cleaned = trimmed.replace(/^[-‚Ä¢*]\s+/, '').trim();

        if (cleaned.length > 10) {
          actionItems.push(cleaned);
          itemsFound++;
          console.log(`    ${itemsFound}. ${cleaned.substring(0, 60)}...`);
        }
      }
      // Check for numbered items (1., 2., etc.)
      else if (trimmed.match(/^\d+\.\s+/)) {
        const cleaned = trimmed.replace(/^\d+\.\s+/, '').trim();

        if (cleaned.length > 10) {
          actionItems.push(cleaned);
          itemsFound++;
          console.log(`    ${itemsFound}. ${cleaned.substring(0, 60)}...`);
        }
      }
    }

    console.log(`  Total items from this section: ${itemsFound}`);
  }
}

// Remove duplicates (case-insensitive comparison)
const uniqueItems = [];
const seenItems = new Set();

for (const item of actionItems) {
  const normalized = item.toLowerCase().replace(/\s+/g, ' ').trim();
  if (!seenItems.has(normalized)) {
    uniqueItems.push(item);
    seenItems.add(normalized);
  }
}

actionItems = uniqueItems;

// ‚úÖ CLEANUP: Remove section headers and pollution (PRESERVE TIME FORMAT!)
console.log(`\n${'='.repeat(50)}`);
console.log(`üßπ CLEANING UP ${actionItems.length} ITEMS`);
console.log(`${'='.repeat(50)}`);

actionItems = actionItems.map((item, index) => {
  const original = item;

  // Check if item contains time format before cleaning
  const hasTime = item.match(/\d{1,2}(?::\d{2})?\s*(?:AM|PM|am|pm)/i);
  if (hasTime) {
    console.log(`  [${index + 1}] ‚è∞ Contains time: ${hasTime[0]}`);
  }

  let cleaned = item
    // Remove section headers with anything after them (but not time indicators!)
    .replace(/\s{2,}(?!AM|PM|am|pm)[A-Z][A-Z\s]+:.*$/gi, '')
    // Remove standalone section markers at the end (but not AM/PM)
    .replace(/\s+(?!AM|PM|am|pm)[A-Z][A-Z\s]+\s*$/i, '')
    .trim();

  // Verify time is still there after cleaning
  const timeAfter = cleaned.match(/\d{1,2}(?::\d{2})?\s*(?:AM|PM|am|pm)/i);
  if (hasTime && !timeAfter) {
    console.log(`  ‚ö†Ô∏è WARNING: Time was removed during cleanup!`);
    console.log(`    Original: "${original}"`);
    console.log(`    Cleaned: "${cleaned}"`);
    // Restore original if time was lost
    cleaned = original;
  }

  if (original !== cleaned) {
    console.log(`  [${index + 1}] Cleaned:`);
    console.log(`    Before: "${original.substring(0, 80)}..."`);
    console.log(`    After:  "${cleaned.substring(0, 80)}..."`);
  }

  return cleaned;
});

// ‚úÖ FILTER: Remove items that are clearly not action items
console.log(`\n${'='.repeat(50)}`);
console.log(`üîç FILTERING OUT NON-ACTION ITEMS`);
console.log(`${'='.repeat(50)}`);

actionItems = actionItems.filter((item, index) => {
  const lower = item.toLowerCase();

  // Skip if it starts with common non-action phrases
  const invalidStarts = [
    'competition with',
    'weather concerns',
    'limited ',
    'short timeline',
    'need more',
    'dependency on',
    'third-party',
    'tight deadline',
    'concerns about',
    'risks include',
    'blocker:',
    'obstacle:',
    'challenge:',
    'insufficient',
    'lack of',
    'not enough'
  ];

  for (const phrase of invalidStarts) {
    if (lower.startsWith(phrase)) {
      console.log(`  ‚úó [${index + 1}] Filtered: "${item.substring(0, 70)}..."`);
      console.log(`      Reason: Starts with "${phrase}"`);
      return false;
    }
  }

  // Skip if it contains section header keywords
  const sectionKeywords = [
    'checkpoints:',
    'project requirements:',
    'deliverables:',
    'next steps:',
    'follow-up items:',
    'obstacles identified:',
    'risks:',
    'concerns:',
    'challenges:'
  ];

  for (const keyword of sectionKeywords) {
    if (lower.includes(keyword)) {
      console.log(`  ‚úó [${index + 1}] Filtered: "${item.substring(0, 70)}..."`);
      console.log(`      Reason: Contains section header "${keyword}"`);
      return false;
    }
  }

  // Skip if too short
  if (item.length < 15) {
    console.log(`  ‚úó [${index + 1}] Filtered: "${item}"`);
    console.log(`      Reason: Too short (${item.length} chars)`);
    return false;
  }

  // Skip if it's just a section header remnant
  if (lower.match(/^(risks?|concerns?|obstacles?|blockers?|challenges?|issues?)\s*$/i)) {
    console.log(`  ‚úó [${index + 1}] Filtered: "${item.substring(0, 70)}..."`);
    console.log(`      Reason: Section header remnant`);
    return false;
  }

  // ‚úÖ MUST contain an action verb OR a date
  const hasActionVerb = lower.match(/\b(to\s+(finalize|create|submit|request|deliver|complete|obtain|launch|conduct|review|send|update|prepare|schedule|organize|coordinate|develop|implement|test|deploy|design|build|analyze|present|approve|sign|configure|setup|install))/i);

  const hasDate = lower.match(/\b(january|february|march|april|may|june|july|august|september|october|november|december)\s+\d{1,2},?\s+\d{4}/i) ||
                  lower.match(/\b(by|on|before|until|starting)\s+(monday|tuesday|wednesday|thursday|friday|saturday|sunday)/i);

  if (!hasActionVerb && !hasDate) {
    console.log(`  ‚úó [${index + 1}] Filtered: "${item.substring(0, 70)}..."`);
    console.log(`      Reason: No action verb or specific date found`);
    return false;
  }

  return true;
});

console.log(`\n${'='.repeat(50)}`);
console.log(`‚úÖ FINAL COUNT: ${actionItems.length} clean action items`);
console.log(`${'='.repeat(50)}\n`);

// Final verification: Log items with time
console.log('\n‚è∞ ITEMS WITH TIME:');
actionItems.forEach((item, idx) => {
  const timeMatch = item.match(/\d{1,2}(?::\d{2})?\s*(?:AM|PM|am|pm)/i);
  if (timeMatch) {
    console.log(`  [${idx + 1}] Time: ${timeMatch[0]} - "${item.substring(0, 60)}..."`);
  }
});

return [{
  json: {
    summary: summary,
    action_items: actionItems,
    formatted_notes: formattedNotes,
    total_actions: actionItems.length
  }
}];

const inputs = $input.all();

console.log('=== MERGE NODE OUTPUT ===');
console.log('Inputs:', inputs.length);

let originalNotes = '';
let summary = '';

// Try to get notes from first input
if (inputs[0]) {
  originalNotes = inputs[0].json?.notes || inputs[0].notes || '';
  console.log('‚úì Found notes, length:', originalNotes.length);
}

// Try to get summary from second input
if (inputs[1]) {
  const j = inputs[1].json || inputs[1];
  let s = '';

  // Huggingface
  if (typeof j.summary_text === 'string' && j.summary_text.length > 0) {
    s = j.summary_text;
  } else if (Array.isArray(j.output)) {
    // GPT
    // j.output[0].content[0].text
    const firstMsg = j.output[0];
    if (firstMsg && Array.isArray(firstMsg.content)) {
      const firstContent = firstMsg.content[0];
      if (firstContent && typeof firstContent.text === 'string') {
        s = firstContent.text;
      }
    }
  }

  summary = s || '';
  console.log('‚úì Found summary, length:', summary.length);
}


// Format notes
let formattedNotes = originalNotes
  .replace(/([A-Z][a-z]+ [A-Z][a-z]+:)/g, '\n$1\n')
  .replace(/([A-Z][a-z]+:)/g, '\n$1\n')
  .replace(/\s+-\s+/g, '\n- ')
  .replace(/\n+/g, '\n')
  .trim();

return [{
  json: {
    formatted_notes: formattedNotes,
    summary: summary,
    original_notes: originalNotes
  }
}];

const ai = $input.first().json;
const parsed = $('Process Updates').first().json;

let msg = ai.output || ai.text || 'Done!';

// Fix literal \n to actual newlines
msg = msg.replace(/\\n/g, '\n');

// Replace Markdown-breaking asterisks with bullets
msg = msg.replace(/^\* /gm, '‚Ä¢ ');

// Escape other Markdown special characters that might cause issues
function escapeMarkdown(text) {
  return text
    .replace(/([_\[\]()~`>#+\-=|{}.!])/g, '\\$1');
}

// Don't escape if message already has emoji at start
if (!msg.match(/^[\u{1F300}-\u{1F9FF}]/u)) {
  if (msg.toLowerCase().includes('created')) msg = '‚úÖ ' + msg;
  else if (msg.toLowerCase().includes('deleted')) msg = 'üóëÔ∏è ' + msg;
  else if (msg.toLowerCase().includes('updated')) msg = '‚úèÔ∏è ' + msg;
  else if (msg.toLowerCase().includes('events')) msg = 'üìÖ ' + msg;
}

// Apply Markdown escaping (but preserve emojis and bullets)
const lines = msg.split('\n');
const escapedLines = lines.map(line => {
  // Don't escape lines that start with emoji or bullet
  if (line.match(/^[\u{1F300}-\u{1F9FF}‚Ä¢]/u)) {
    return line;
  }
  return escapeMarkdown(line);
});
msg = escapedLines.join('\n');

console.log('Final message:', msg);

return [{
  json: {
    chatId: parsed.chatId.toString(),
    text: msg,
    parse_mode: 'Markdown'
  }
}];

const res = $input.first().json;

// Check if we got valid updates
if (! res.ok || !res.result) {
  console.log('No valid response');
  return [];
}

const updates = res.result;

// If no messages, return empty
if (updates.length === 0) {
  console. log('No new messages');
  return [];
}

console.log('üì¨ Processing', updates. length, 'updates');

// Process messages
const out = [];

for (const u of updates) {
  const m = u.message || u.channel_post;

  if (!m) continue;

  const chatId = m.chat.id;
  const usr = m.from?. username || m.chat.title || 'User';

  // ‚úÖ SIMPLIFIED: Check if document exists
  if (m.document) {
    const doc = m.document;
    const fileName = doc.file_name || 'unknown';
    const fileId = doc.file_id;
    const mimeType = doc.mime_type || '';
    const fileSize = doc. file_size || 0;

    console.log(`üìé File received: ${fileName} (${fileSize} bytes)`);

    // Check file size (max 20MB)
    if (fileSize > 20 * 1024 * 1024) {
      out.push({
        json: {
          chatId: chatId,
          messageText: '/error',
          text: '‚ùå File too large (max 20MB)\n\nPlease upload a smaller file.',
          parse_mode: 'Markdown',
          skipAI: true,
          isMeetingNotes: false,
          isDocument: false
        }
      });
      continue;
    }

    // ‚úÖ If document exists, set isDocument: true (no MIME type check!)
    console.log(`‚úÖ Document detected - isDocument: true`);

    out.push({
      json: {
        chatId: chatId,
        username: usr,
        messageText: `[FILE: ${fileName}]`,
        fileId: fileId,
        fileName: fileName,
        mimeType: mimeType,
        fileSize: fileSize,
        fileType: fileName.toLowerCase().endsWith('.pdf') ? 'pdf' : 'docx',
        isDocument: true,  // ‚úÖ Always true if document exists
        sessionId: `tg_${chatId}`,
        skipAI: false,
        isMeetingNotes: false
      }
    });
    continue;
  }

  // ‚úÖ PRESERVED: Existing text message handling
  const txt = m.text?.trim();

  if (!txt) continue;

  console.log('üí¨ Message:', txt. substring(0, 100));

  // Handle /start
  if (txt === '/start' || txt.startsWith('/start@')) {
    out.push({
      json: {
        chatId: chatId,
        messageText: txt,
        text: 'üëã *Welcome to Calendar Bot!*\n\nI help manage your Google Calendar and summarize meetings.\n\n*Features:*\n‚Ä¢ üìÖ "Schedule meeting tomorrow 3 PM"\n‚Ä¢ üìù Paste meeting notes to get summary + calendar events\n‚Ä¢ üìÑ Upload PDF/DOCX files for automatic processing\n‚Ä¢ üóëÔ∏è "Delete meeting on Nov 27"\n‚Ä¢ /help\n\nType naturally!  üí¨',
        parse_mode: 'Markdown',
        skipAI: true,
        isMeetingNotes: false,
        isDocument: false
      }
    });
    continue;
  }

  // Handle /help
  if (txt === '/help' || txt.startsWith('/help@')) {
    out.push({
      json: {
        chatId: chatId,
        messageText: txt,
        text: 'ü§ñ *Help*\n\n*üìù Calendar:*\n‚Ä¢ Schedule meeting tomorrow 9 AM\n‚Ä¢ What\'s on my calendar?\n‚Ä¢ Delete meeting on Nov 27\n‚Ä¢ Move meeting to 4 PM\n\n*üìã Meeting Summary:*\n‚Ä¢ Paste meeting notes (>200 chars)\n‚Ä¢ Upload PDF or DOCX files\n‚Ä¢ Get AI summary\n‚Ä¢ Auto-create calendar events\n\nType naturally! üí¨',
        parse_mode: 'Markdown',
        skipAI: true,
        isMeetingNotes: false,
        isDocument: false
      }
    });
    continue;
  }

  // Skip other commands
  if (txt.startsWith('/')) continue;

  // ‚úÖ PRESERVED: Detect meeting notes (long message with keywords)
  const isMeetingNotes = txt.length > 200 && (
    txt.toLowerCase(). includes('meeting') ||
    txt. toLowerCase().includes('attendees') ||
    txt.toLowerCase().includes('action item') ||
    txt. toLowerCase().includes('deliverable') ||
    txt.toLowerCase().includes('next step') ||
    txt. toLowerCase().includes('project') ||
    txt.toLowerCase().includes('discussion')
  );

  if (isMeetingNotes) {
    console.log('üìù Detected MEETING NOTES (length:', txt.length, ')');
    out.push({
      json: {
        chatId: chatId,
        username: usr,
        messageText: txt,
        notes: txt,
        sessionId: `tg_${chatId}`,
        skipAI: false,
        isMeetingNotes: true,
        isDocument: false
      }
    });
  } else {
    // ‚úÖ PRESERVED: Regular calendar command
    console.log('üìÖ Regular calendar command');
    out.push({
      json: {
        chatId: chatId,
        username: usr,
        messageText: txt,
        input: txt,
        sessionId: `tg_${chatId}`,
        skipAI: false,
        isMeetingNotes: false,
        isDocument: false
      }
    });
  }
}

console.log('üì§ Returning', out.length, 'messages');
return out;

// Get saved offset
let offset = 0;

try {
  if (typeof $workflow !== 'undefined' && $workflow.staticData !== undefined) {
    offset = $workflow.staticData.telegramOffset || 0;
    console.log('üìç Using offset:', offset);
  }
} catch (e) {
  console.log('‚ö†Ô∏è Error:', e.message);
}

return [{
  json: {
    offset: offset
  }
}];

// Consolidate alerts - Show ALL upcoming events (meetings AND tasks)
const items = $input.all();

console.log(`=== UPCOMING EVENTS & TASKS ===`);
console.log(`Processing ${items.length} events`);

const now = new Date();

function normalizeDateTime(dt) {
  if (!dt) return '';

  if (typeof dt === 'string') {
    if (dt.length >= 19) return dt.slice(0, 19);
    return dt;
  }

  if (typeof dt === 'object') {
    if (dt.dateTime) {
      const s = dt.dateTime;
      return s.length >= 19 ? s.slice(0, 19) : s;
    }
    if (dt.date) {
      return dt.date + 'T09:00:00';
    }
  }

  return '';
}

function isAllDayEvent(startRaw) {
  if (typeof startRaw === 'object' && startRaw.date) {
    return true;
  }
  return false;
}

function formatDateTime(dateStr, isAllDay, showTime = false) {
  if (!dateStr) return 'Unknown';

  const date = new Date(dateStr);

  if (isAllDay || !showTime) {
    // Just show date
    return date.toLocaleDateString('en-US', { 
      weekday: 'short', 
