    });
  }
}

function getTimeUntil(dateStr) {
  if (!dateStr) return '';

  const target = new Date(dateStr);
  const diffMs = target - now;

  if (diffMs < 0) return '‚ö†Ô∏è OVERDUE';

  const diffHours = Math.floor(diffMs / (1000 * 60 * 60));
  const diffDays = Math.floor(diffHours / 24);
  const diffWeeks = Math.floor(diffDays / 7);
  const diffMonths = Math.floor(diffDays / 30);

  if (diffHours < 1) return 'üö® Less than 1 hour';
  if (diffHours < 24) return `üö® ${diffHours} hour${diffHours > 1 ? 's' : ''}`;
  if (diffDays === 1) return `‚ö†Ô∏è Tomorrow`;
  if (diffDays < 7) return `‚ö†Ô∏è ${diffDays} day${diffDays > 1 ? 's' : ''}`;
  if (diffWeeks < 4) return `üìÖ ${diffWeeks} week${diffWeeks > 1 ? 's' : ''}`;
  if (diffMonths < 12) return `üìÖ ${diffMonths} month${diffMonths > 1 ? 's' : ''}`;

  return `üìÖ ${Math.floor(diffDays / 365)} year${Math.floor(diffDays / 365) > 1 ? 's' : ''}`;
}

function getUrgencyLevel(dateStr) {
  if (!dateStr) return 'low';

  const target = new Date(dateStr);
  const diffHours = (target - now) / (1000 * 60 * 60);

  if (diffHours < 0) return 'overdue';
  if (diffHours <= 24) return 'urgent';
  if (diffHours <= 168) return 'soon'; // 7 days
  return 'upcoming';
}

